import React, { useEffect } from 'react';
import Nav from '../components/Nav';
import ChatBox from '../components/Chatbox';

const Main = () => {
  useEffect(() => {
    // Simple JS for sidebar toggle
    const toggleBtn = document.getElementById('sidebarToggle');
    const sidebar = document.getElementById('sidebar');

    toggleBtn.addEventListener('click', () => {
      sidebar.classList.toggle('d-none');
    });
  }, []);

  return (
    <div className="container-fluid">
      <div className="row">
        {/* Sidebar */}
        <div
          id="sidebar"
          className="col-md-3 col-lg-2 bg-dark text-white p-3 d-none d-md-block"
          style={{ minHeight: '100vh' }}
        >
          <h4 className="text-center">Menu</h4>
          <ul className="list-group">
            <li className="list-group-item bg-dark text-white border-0">
              <a href="#" className="text-white text-decoration-none">Dashboard</a>
            </li>
            <li className="list-group-item bg-dark text-white border-0">
              <h5>Recents</h5>
            </li>
            <li className="list-group-item bg-dark text-white border-0">
              <h5>History</h5>
            </li>
          </ul>
        </div>

        {/* Main Content */}
        <div className="col p-3">
          {/* Hamburger Button */}
          <button
            id="sidebarToggle"
            className="btn btn-dark d-md-none mb-3"
          >
            &#9776; Menu
          </button>

          <Nav />
          <ChatBox />
        </div>
      </div>
    </div>
  );
};

export default Main;
