import React, { useState, useRef, useEffect } from 'react';

const Chatbox = () => {
  const [messages, setMessages] = useState([
    { sender: 'ai', text: 'Hi! I’m your AI study assistant. Ask me anything!' }
  ]);
  const [input, setInput] = useState('');
  const messagesEndRef = useRef(null);

  const handleSend = () => {
    if (input.trim() === '') return;

    const newMessages = [
      ...messages,
      { sender: 'user', text: input },
      { sender: 'ai', text: `AI response to: "${input}"` } // Replace with actual AI response logic
    ];
    setMessages(newMessages);
    setInput('');
  };

  const handleKeyDown = (e) => {
    if (e.key === 'Enter' && !e.shiftKey) {
      e.preventDefault();
      handleSend();
    }
  };

  useEffect(() => {
    messagesEndRef.current?.scrollIntoView({ behavior: 'smooth' });
  }, [messages]);

  return (
    <div className="d-flex flex-column" style={{ height: '100vh' }}>
      {/* Chat Display */}
      <div className="flex-grow-1 overflow-auto p-3 bg-light">
        {messages.map((msg, idx) => (
          <div
            key={idx}
            className={`mb-3 p-2 rounded ${msg.sender === 'user' ? 'bg-primary text-white text-end ms-auto' : 'bg-secondary text-white me-auto'}`}
            style={{ maxWidth: '75%' }}
          >
            {msg.text}
          </div>
        ))}
        <div ref={messagesEndRef}></div>
      </div>

      {/* Input Area */}
      <div className="p-3 border-top bg-white">
        <textarea
          className="form-control mb-2"
          placeholder="Type your message here..."
          rows={2}
          value={input}
          onChange={(e) => setInput(e.target.value)}
          onKeyDown={handleKeyDown}
        />
        <button className="btn btn-success w-100" onClick={handleSend}>
          Send
        </button>
      </div>
    </div>
  );
};

export default Chatbox;
