import React, { useRef } from 'react';
import { GoogleGenAI } from '@google/genai';

const ai = new GoogleGenAI({
  apiKey: 'AIzaSyAiV_SF4k-qRDPTDPBVEFMQ9d8eM1KLjoU',
});

const Chatbox = () => {
  const inputRef = useRef(null);

  const handleSend = async () => {
    const msg = inputRef.current.value;
    if (!msg.trim()) return;

    try {
      const response = await ai.models.generateContent({
        model: 'gemini-2.0-flash',
        contents: msg,
      });

      console.log(response.text); // or update state to show bot response
    } catch (error) {
      console.error('Error generating content:', error);
    }
  };

  return (
    <div>
      <div className="container">
        <div className="row">
          <div className="box">
            <div className="chat-body">
              <div className="chat-message user">
                <span className="badge bg-primary">You:</span> Hello!
              </div>
              <div className="chat-message bot">
                <span className="badge bg-secondary">Bot:</span> Hi there! How can I help?
              </div>
            </div>
            <div className="chat-footer d-flex">
              <input
                type="text"
                className="form-control me-2"
                placeholder="Type your message..."
                ref={inputRef}
              />
              <button className="btn btn-primary" onClick={handleSend}>
                Send
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
};

export default Chatbox;
