// ChatBot.jsx
import React, { useState } from 'react';
import axios from 'axios';

const ChatBot = () => {
  const [messages, setMessages] = useState([
    { role: 'bot', content: 'Hi there! How can I help?' }
  ]);
  const [input, setInput] = useState('');

  const sendMessage = async () => {
    if (!input.trim()) return;

    const userMessage = { role: 'user', content: input };
    setMessages(prev => [...prev, userMessage]);
    setInput('');

    try {
      const response = await axios.post(
        'https://api.openai.com/v1/chat/completions',
        {
          model: 'gpt-3.5-turbo',
          messages: [
            { role: 'system', content: 'You are a study assistant.' },
            ...messages,
            userMessage
          ]
        },
        {
          headers: {
            Authorization: `Bearer YOUR_AI_STUDIO_API_KEY`,
            'Content-Type': 'application/json'
          }
        }
      );

      const reply = response.data.choices[0].message;
      setMessages(prev => [...prev, reply]);

    } catch (err) {
      console.error(err);
      setMessages(prev => [
        ...prev,
        { role: 'bot', content: 'Oops! Something went wrong.' }
      ]);
    }
  };

  return (
    <div className="flex flex-col h-full">
      <div className="flex-1 p-4 overflow-y-auto">
        {messages.map((msg, i) => (
          <div key={i} className={`mb-2 ${msg.role === 'user' ? 'text-right' : 'text-left'}`}>
            <span className="px-3 py-2 rounded-lg inline-block bg-gray-200 dark:bg-gray-700">
              <strong>{msg.role === 'user' ? 'You' : 'Bot'}:</strong> {msg.content}
            </span>
          </div>
        ))}
      </div>
      <div className="p-4 border-t flex">
        <input
          type="text"
          className="flex-1 px-3 py-2 border rounded-l-md"
          value={input}
          onChange={(e) => setInput(e.target.value)}
          placeholder="Type your message..."
        />
        <button
          className="bg-blue-600 text-white px-4 py-2 rounded-r-md"
          onClick={sendMessage}
        >
          Send
        </button>
      </div>
    </div>
  );
};

export default ChatBot;
