// src/pages/Main.jsx
import React, { useEffect } from 'react';
import Sidebar from '../components/Sidebar';
import Nav from '../components/Nav';
import ChatBox from '../components/Chatbox';

const Main = () => {
  useEffect(() => {
    const toggleBtn = document.getElementById('sidebarToggle');
    const sidebar = document.getElementById('sidebar');
    const overlay = document.getElementById('sidebarOverlay');

    toggleBtn.addEventListener('click', () => {
      sidebar.classList.toggle('d-none');
      overlay.classList.toggle('d-none');
    });

    overlay.addEventListener('click', () => {
      sidebar.classList.add('d-none');
      overlay.classList.add('d-none');
    });
  }, []);

  return (
    <>
      {/* Dark overlay */}
      <div
        id="sidebarOverlay"
        className="position-fixed top-0 start-0 w-100 h-100 bg-dark bg-opacity-50 d-none"
        style={{ zIndex: 998 }}
      ></div>

      {/* Sidebar */}
      <div
        id="sidebar"
        className="position-fixed top-0 start-0 bg-dark text-white p-3 d-none"
        style={{ width: '250px', height: '100vh', zIndex: 999 }}
      >
        <Sidebar />
      </div>

      {/* Main content */}
      <div className="container-fluid">
        <div className="row">
          <div className="col p-3">
            {/* Hamburger button */}
            <button id="sidebarToggle" className="btn btn-dark mb-3">
              &#9776; Menu
            </button>

            <Nav />
            <ChatBox />
          </div>
        </div>
      </div>
    </>
  );
};

export default Main;
